import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import API from '../api/api';

export default function RequestDetails() {
  const { id } = useParams();
  const [request, setRequest] = useState(null);

  useEffect(() => { API.get(`/requests/${id}`).then(r => setRequest(r.data)); }, [id]);
  if (!request) return <p>Loading...</p>;

  return (
    <div className="card">
      <h2>{request.title}</h2>
      <p>{request.description}</p>
      <p>Status: {request.status}</p>
      <div>
        <h3>Photos</h3>
        {request.photos?.map(p => <img key={p} src={(process.env.REACT_APP_API_URL || 'http://localhost:5000').replace('/api','') + p} alt="photo" style={{ maxWidth:200 }} />)}
      </div>
      <div>
        <h3>Comments</h3>
        {request.comments?.map(c => <div key={c._id}><b>{c.author?.name}</b>: {c.message}</div>)}
      </div>
    </div>
  );
}
